import React, { useState } from 'react'
import './CourseInput.css'
import styled from 'styled-components'
import Button from '../../UI/Button/Button'

const FormControl = styled.div`
  margin: 0.5rem 0;

  & label {
    font-weight: bold;
    display: block;
    margin-bottom: 0.5rem;
  }

  & input {
    display: block;
    width: 100%;
    border: 1px solid #ccc;
    font: inherit;
    line-height: 1.5rem;
    padding: 0 0.25rem;
  }

  & input:focus {
    outline: none;
    background: #fad0ec;
    border-color: #8b005d;
  }

  &.invalid label {
    color: red;
  }
  &.invalid input {
    border-color: red;
    background-color: rgb(245, 212, 212);
  }
`

const CourseInput = (props) => {
  const [enteredValue, setEnteredValue] = useState('')
  const [isValid, setIsValid] = useState(true) //default state

  const formSubmitHandler = (event) => {
    event.preventDefault()

    if (enteredValue.trim().length === 0) {
      setIsValid(false)
      return //return'den sonrası execute edilmez.
    }
    if (enteredValue.trim().length > 0) {
      setIsValid(true)
    }

    props.onAddGoal(enteredValue)
  }
  const goalInputChangeHandler = (event) => {
    setEnteredValue(event.target.value)
  }
  // ``: template literal ( a JS feature)
  // template literal içerisinde "$" sign kullanılarak "invalid" class'ı dinamik hale getirildi.
  return (
    <form onSubmit={formSubmitHandler}>
      <div className={`form-control ${!isValid ? 'invalid' : ''}`}>
        <label>Course Goal</label>
        <input type="text" onChange={goalInputChangeHandler}></input>
      </div>
      <Button type="submit">Add Goal</Button>
    </form>
  )
}

export default CourseInput
